$(document).ready(function(){
  // Back to Top link
  var backToTop = $('.back-to-top');

  backToTop.click(function(){
    $('html,body').animate({
      scrollTop: 0
    }, "slow");
    return false;
  });
});

/*!
 * @author Tanju Yildiz <ben@tanjuyildiz.com> 
 */
jQuery(document).ready(function(){
  /**
   * Shrinking Navbar
   */
  var siteHeader   = $('#header');
  var sticky       = $('#header .sticky');
  var scrollLength = siteHeader.height() + 30;

  $(window).on("scroll", function() {
    sticky.toggleClass("fixed", $(this).scrollTop() > scrollLength);
  });
  $('#menu').on('show.bs.collapse', function() {
    sticky.addClass("fixed");
  });
  $('#menu').on('hide.bs.collapse', function() {
    sticky.removeClass("fixed");
  });

  /**
   * Sub-menu
   */
  var navbarDropdown       = $("#header .navbar .dropdown");
  var navbarDropdownToggle = $("#header .navbar .dropdown-toggle");

  var peDropdown = function(element) {
    var $this      = $(this);
    var $parent    = $this.parent("li");
    var $parentAll = $parent.parent(".navbar-nav").find(".dropdown");
    var isActive   = $parent.hasClass('open');

    $parentAll
      .removeClass("open")
      .trigger("hide.bs.dropdown")
      .trigger("hidden.bs.dropdown");

    if (!isActive) {
      $this.attr('aria-expanded', 'true');
      $parent
        .addClass('open')
        .trigger('shown.bs.dropdown');
    } else {
      $this.attr('aria-expanded', 'false');
      $parent
        .removeClass("open")
        .trigger('hide.bs.dropdown');
    }
  };

  if ($(window).width() >= 768) {
    var currentMenuAnchestor = $('#menu > ul.navbar-nav > li.current-menu-ancestor');
    var currentMenuItem      = $('#menu > ul.navbar-nav > li.current-menu-item');
    var isHeaderFixed = false;

    if (currentMenuAnchestor.length > 0) {
      var fixedMenuID = $('#menu > ul.navbar-nav > li.current-menu-ancestor > a.dropdown-toggle').attr('data-rel');
      isHeaderFixed = true;
    } else if (currentMenuItem.length > 0) {
      var fixedMenuID = $('#menu > ul.navbar-nav > li.current-menu-item > a.dropdown-toggle').attr('data-rel');
      isHeaderFixed = true;
    }

    if (isHeaderFixed !== false) {
      var $menuID = fixedMenuID;

      siteHeader.addClass("fixed");

      $(".header-bottom").addClass("selected");
      $("#" + $menuID).addClass("selected");

      $('#menu > ul.navbar-nav > li.current-menu-ancestor > a.dropdown-toggle[data-rel="' + $menuID + '"]')
        .parent()
        .addClass("hover");
    }

    navbarDropdown.hover(function() {
      $this   = $(this);
      $menuID = $this.find("a").attr("data-rel");

      siteHeader.addClass("hover");

      $("#" + $menuID).addClass("active");

      $('#header .navbar .dropdown-toggle[data-rel="' + $menuID + '"]')
        .parent()
        .addClass("hover");

      $(".header-bottom .submenu-wrapper").off("mouseenter mouseleave");
    }, function() {
      $('#' + $menuID).removeClass("active");

      $('#header .navbar .dropdown-toggle[data-rel="' + $menuID + '"]')
        .parent()
        .removeClass("hover");

      siteHeader.removeClass("hover");

      $(".header-bottom").hover(function() {
        if ($menuID != "") {
          siteHeader.addClass("hover");

          $menuID = $this.find("a").attr("data-rel");

          $("#" + $menuID).addClass("active");

          $('#header .navbar .dropdown-toggle[data-rel="' + $menuID + '"]')
            .parent()
            .addClass("hover");
        }
      }, function() {
        $('#' + $menuID).removeClass("active");

        $('#header .navbar .dropdown-toggle[data-rel="' + $menuID + '"]')
          .parent()
          .removeClass("hover");

        $(".header-bottom .submenu-wrapper").off("mouseenter mouseleave");

        $menuID = "";

        siteHeader.removeClass("hover");
      });
    });
  } else {
    navbarDropdownToggle.on("click", peDropdown);
  }

  /**
   * Featured Slider
   */
  var slider = $("#featuredSlider");
  var sliderPrev = $(".slider .slider-nav-left");
  var sliderNext = $(".slider .slider-nav-right");

  slider.owlCarousel({
    autoplay: true,
    autoplayHoverPause: true,
    loop: (slider.find(".item").length > 1 ? true : false),
    items: 1,
    nav: false,
    dotsClass: "wrapper owl-dots"
  });
  sliderPrev.click(function(e) {
    e.preventDefault();
    slider.trigger('prev.owl.carousel');
  });
  sliderNext.click(function(e) {
    e.preventDefault();
    slider.trigger('next.owl.carousel');
  });

  /**
   * Recent News
   */
  var recentNews = $("#recentNews");

  recentNews.owlCarousel({
    autoplay: true,
    autoplayHoverPause: true,
    dots: false,
    loop: (slider.find(".item").length > 1 ? true : false),
    items: 1,
    nav: false,
  });
});
